import React, { useState } from 'react';
import axios from 'axios';

const AddSensor = ({ onSensorAdded }) => {
  const [sensorName, setSensorName] = useState('');

  const handleAddSensor = async () => {
    try {
      const response = await axios.post('http://localhost:5000/Sensors', { name: sensorName });
      onSensorAdded(response.data); // Notify parent component about the added sensor
      setSensorName(''); // Clear input field
    } catch (error) {
      console.error('Error adding sensor:', error);
    }
  };

  return (
    <div>
      <h2>Add Sensor</h2>
      <input
        type="text"
        placeholder="Sensor Name"
        value={sensorName}
        onChange={(e) => setSensorName(e.target.value)}
      />
      <button onClick={handleAddSensor}>Add Sensor</button>
    </div>
  );
};

export default AddSensor;
