import './App.css';
import React, { useState } from 'react';
import Navigation from './Navigation';
import MainContent from './MainContent';

function App() {
  const [showSensors, setShowSensors] = useState(false);
  const [showRT, setShowRT] = useState(false);
  const [showHistorical, setShowHistorical] = useState(false);
  const [showAnalytics, setShowAnalytics] = useState(false);

  const handleButtonClick = () => {
    // Toggle the value of showSensorList
    setShowRT(false);
    setShowHistorical(false);
    setShowAnalytics(false);
    setShowSensors(true);
  };

  const handleRTButtonClick = () => {
    // Toggle the value of showSensorList
    setShowSensors(false);
    setShowHistorical(false);
    setShowAnalytics(false);
    setShowRT(true);
  };

  const handleHistoricalButtonClick = () => {
    // Toggle the value of showSensorList
    setShowSensors(false);
    setShowRT(false);
    setShowAnalytics(false);
    setShowHistorical(true);
  };

  const handleAnalyticsButtonClick = () => {
    // Toggle the value of showSensorList
    setShowSensors(false);
    setShowRT(false);
    setShowHistorical(false);
    setShowAnalytics(true);
  };

  return (
    <div className="App">
      <Navigation
        onButtonClick={handleButtonClick}
        onRTButtonClick={handleRTButtonClick}
        onHistoricalButtonClick={handleHistoricalButtonClick}
        onAnalyticsButtonClick={handleAnalyticsButtonClick}
      />
      <MainContent
        showSensors={showSensors}
        showRT={showRT}
        showHistorical={showHistorical}
        showAnalytics={showAnalytics}
      />
    </div>
  );
}

export default App;
