import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import SensorList from './SensorList';
import OfflineSensors from './OfflineSensors';
import AddSensor from './AddSensor';

function App() {
  const [sensors, setSensors] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/sensors');
        setSensors(response.data);
      } catch (error) {
        console.error('Error fetching sensors:', error);
      }
    };

    fetchData();
  }, []);

  const handleSensorAdded = (newSensor) => {
    setSensors((prevSensors) => [...prevSensors, newSensor]);
  };

  return (
    <div className="App">
      <Router>
        <Routes>
            path="/add"
            render={() => <AddSensor onSensorAdded={handleSensorAdded} />}
          />
          <Route path="/offline-sensors" element={<OfflineSensors />} />
          <Route path="/" element={<SensorList />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
