import React, { useEffect } from 'react';

const RealTime = () => {
  useEffect(() => {
    // Initialize Firebase with your Firebase project configuration
    firebase.initializeApp({
        apiKey: "AIzaSyCSrHx7q_pEZaWuOsnLxlqh6be7TWPl6GM",
        authDomain: "swe4103-db.firebaseapp.com",
        databaseURL: "https://swe4103-db-default-rtdb.firebaseio.com/",
        projectId: "swe4103-db",
        storageBucket: "swe4103-db.appspot.com",
        messagingSenderId: "",
        appId: ""
    });

    // Sign in anonymously
    firebase.auth().signInAnonymously()
    .then((userCredential) => {
        var user = userCredential.user;
        // The user is signed in anonymously
    })
    .catch((error) => {
        var errorCode = error.code;
        var errorMessage = error.message;
        // Handle errors here
    });

    firebase.auth().onAuthStateChanged((user) => {
        if (user) {
        // User is signed in (either anonymously or with another method)
        var uid = user.uid;
        } else {
        // User is signed out
        }
    });

    const dataDiv = document.getElementById('data');

    // Listen for changes in real-time data
    const ref = firebase.database().ref('/energydata');
    ref.on('value', (snapshot) => {
        const data = snapshot.val();
        dataDiv.innerHTML = ""
        for (var key in data) {
            var sensorEntry = data[key];

            // Create a new element to display the sensor data
            var sensorElement = document.createElement("div");
            sensorElement.textContent = `Timestamp: ${sensorEntry.timestamp}, Value: ${sensorEntry.value}`;

            // Append the element to the container
            dataDiv.appendChild(sensorElement);
        }
    }, function(error) {
        console.error(error);
    });
  }, []);

  return (
    <div>
      <h1>Real-time Data</h1>
      <div id="data"></div>
    </div>
  );
};

export default RealTime;
