import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import OfflineSensors from './OfflineSensors';

const SensorList = () => {
  const [sensors, setSensors] = useState([]);
  const [offlineSensors, setOfflineSensors] = useState([]);
  const [updateFormData, setUpdateFormData] = useState({ sensorId: '', timestamp: '', value: '' });

  useEffect(() => {
      // Fetch sensor data from Flask backend
      axios.get('http://localhost:5000/')
        .then(response => {
          const allSensors = response.data;
          const offlineSensors = allSensors.filter(sensor => sensor.value < 1 || sensor.value > 3);
          setSensors(allSensors)
          setOfflineSensors(offlineSensors);
        })
        .catch(error => console.error('Error fetching sensors:', error));
  }, []);

  const handleUpdate = (sensorId, timestamp, value) => {
    // Set initial values for the update form
    setUpdateFormData({ sensorId, timestamp, value });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setUpdateFormData({ ...updateFormData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Your update logic goes here
    const { sensorId, timestamp, value } = updateFormData;

    // You can use axios.post or axios.put to send data to your Flask backend
    // Example: axios.patch(`http://localhost:5000/Sensors/${sensorId}`, { timestamp, value });

    // After submitting, clear the form data
    setUpdateFormData({ sensorId: '', timestamp: '', value: '' });
  };

  return (
    <div>
      {/* ... existing code ... */}
      <h1>Sensor Management System</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Value</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(sensors).map(sensorId => {
            const sensor = sensors[sensorId];
            return (
              <React.Fragment key={sensorId}>
                {sensor.historical_data !== null && (
                  sensor.historical_data.map(row => (
                    <tr key={row.serialnum}>
                      <td>{row.serialnum}</td>
                      <td>{row.timestamp}</td>
                      <td>{row.value}</td>
                      <td>
                        {(updateFormData.sensorId !== sensorId || updateFormData.timestamp !== row.timestamp) && (
                          <button onClick={() => handleUpdate(sensorId, row.timestamp, row.value)}>Update</button>
                        )}
                        {/* Update Form */}
                        {updateFormData.sensorId === sensorId && updateFormData.timestamp === row.timestamp && (
                          <form onSubmit={(e) => handleFormSubmit}>
                            <input type="number" step="0.1" name="value" value={updateFormData.value} onChange={handleFormChange}/>
                            <button type="submit">Submit</button>
                          </form>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </React.Fragment>
            );
          })}
        </tbody>
      </table>

      {/* Link to the Offline Sensors page */}
      <Link to="/offline-sensors">View Offline Sensors</Link>

      {/* Offline Sensors */}
      <OfflineSensors offlineSensors={offlineSensors} />
    </div>
  );
};

export default SensorList;
