import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import SensorList from './SensorList';
import OfflineSensors from './OfflineSensors';
import AddSensor from './AddSensor';

function App() {
  const [sensors, setSensors] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/');
        setSensors(response.data);
      } catch (error) {
        console.error('Error fetching sensors:', error);
      }
    };

    fetchData();
  }, []);

  const handleSensorAdded = (newSensor) => {
    setSensors((prevSensors) => {console.log(prevSensors); return [...prevSensors, newSensor];});
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route
            path="/Sensors"
            element={(
              <div>
                <AddSensor onSensorAdded={handleSensorAdded} />
                <ul>
                  {Object.values(sensors).map((sensor) => (
                    <li key={sensor.serial_number}>
                      {sensor.serial_number}
                      {/*<DeleteSensor
                        sensorId={sensor.id}
                        onSensorDeleted={handleSensorDeleted}
                      />*/}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          />
          <Route path="/Sensors/offline" element={<OfflineSensors />} />
          <Route path="/Sensors/historical" element={<SensorList />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
